import * as actionTypes from './actionTypes';
import axios from 'axios';

export const genre = (genre) => {
    return {
        type: actionTypes.GENRE,
        genre: genre
    };
};

export const ordre = (ordre) => {
    return {
        type: actionTypes.ORDRE,
        ordre: ordre
    };
};

export const years = (years) => {
    return {
        type: actionTypes.YEARS,
        years: years
    };
};

export const textSearch = (textSearch) => {
    return {
        type: actionTypes.TEXT_SEARCH,
        textSearch: textSearch
    }; 
};

export const movies = (movies) => {
    console.log(movies)
    return {
        type: actionTypes.MOVIES,
        movies: movies
    }
};

export const filtres = (filtres) => {
    console.log(filtres);
    return dispatch => {
        axios.get(`https://api.themoviedb.org/3/discover/movie?api_key=1e32f5c452c2267d5367589e9864ab1c&with_genres=${filtres.genre.value}`)
            .then(response => {
                console.log(response);
                dispatch(movies(response.data.result));
            })
            .catch(err => console.log(err))
    }
}
