import React, { Component } from "react";
import axios from "axios";
import InfiniteScroll from "react-infinite-scroll-component";
import { connect } from "react-redux";
import "./MoviesList.css";
import { withRouter } from 'react-router-dom';
import MovieItems from "./MoviesItems/MoviesItems";
import * as actions from '../../../store/actions/index';

import { API_KEY, PATH_BASE, PATH_DISCOVER, PATH_MOVIE, PATH_SEARCH, PATH_PAGE, PATH_ADULT, PATH_LANGUE } from  './api';

class MoviesList extends Component {
  render() {
    let movies = null;
    if (this.state.movies !== null && this.state.movies.length > 0) 
      movies = <MovieItems movies={this.state.movies} clicked={() => this.clicked()}/>
    if (this.state.movies.length === 0)
      movies = <p style={{ color: "white" }}>Il y a 0 resultat pour: "{this.props.textSearch}".</p>

    let asyncPagination = null;

    return (
      <div className="block">
        <InfiniteScroll
          dataLength='1000'
          next={asyncPagination}
          // hasMore={this.state.hasMore}
          loader={<h4>Loading...</h4>}
          endMessage={<p style={{ textAlign: "center" }}><b>Yay! You have seen it all</b></p>}>
          {/* {movies} */}
        </InfiniteScroll>
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log('movies----> ',state.filtres.movies)
  return {
    movies: state.filtres.movies,
    textSearch: state.filtres.textSearch
  };
};

const mapDispatchToProps = dispatch => {
  return {

    actionTextSearch: () => dispatch(actions.textSearch(''))
  };
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(MoviesList));
