import React, { Component } from 'react'
import { Form } from 'react-bootstrap'
import firebase from '../../../../fire';
import { withRouter } from 'react-router-dom';
import * as actions from '../../../../store/actions/index';
import { connect } from 'react-redux';
import MyInput from '../../../../components/MyInput/MyInput';
import MyButton from '../../../../components/MyButton/MyButton';
import '../../auth.css';
import { checkInput } from '../../../../components/utility/utility';

class Phone extends Component {
    state = {
        values: {},
        errors: {},
        errorCatch:'',
        disable: true,
        show: false,
        codeError: null,
        code: false
    }

    handleInput = (e, number) => {
        if (this.state.code === true)

        console.log('id   -->', e.target.id)
        console.log('111  --> ', e.target.value, '    values.phone --> ', this.state.values.codeVerification)
        const Id = e.target.id;
        const values = {...this.state.values};
        values[Id] = e.target.value;
        this.setState({ values : values }, () => {
            let error = null;
            error = checkInput(Id, values[Id]);
            const errors = {...this.state.errors};
            errors[Id]= error;
            this.setState({ errors: errors }, () => {
                Object.values(this.state.errors).join('').length === 0 && Object.keys(this.state.errors).length === number ? 
                this.setState({ disable: false }) : this.setState({ disable: true });
            })
        });
    }

    sendCode = (e) => {  
        e.preventDefault();
        firebase.auth().languageCode = 'fr';
        window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier("recaptcha-container", { size:"invisible" });
        let phoneNumber = this.state.values.phone;
        let initializePhone = {...this.state.values};
        initializePhone["phone"] = null;
        let appVerifier = window.recaptchaVerifier;
        firebase.auth().signInWithPhoneNumber(phoneNumber, appVerifier)
            .then(confirmationResult => { 
                this.setState({ show: true, disable : true, values: initializePhone }, () => {
                    window.confirmationResult = confirmationResult;
                    phoneNumber = null;
                })
            })
            .catch(error => {
                this.setState({errorCatch:error.message})
            });
    }

    receiveCode = (e) => {
        e.preventDefault();
        const verificationId = this.state.values.codeVerification;
        window.confirmationResult
        .confirm(verificationId)
        .then(result => {
            localStorage.setItem('id',result.user.uid)
            localStorage.setItem('token', result.user.ma)
            localStorage.setItem('show', true)
            this.props.onPhone(result.user.ma,result.user.uid);
            this.props.history.push('/home')
        })
        .catch(error => {
            this.setState({codeError:'Error'});
        });
    }

    render() {
        let msgCatch;
        let form; 

        if (this.state.errorCatch && this.state.codeError === null)
            msgCatch = <p>Trop d'essai votre compte a été temporairement suspendu. Reesayer plus tard</p>;

        if (this.state.codeError)
            msgCatch = <p>le code ne coresspond pas a celui qui vous a été envoyer veuillez ressayer</p>;



        return (
            //IL FAUT ENLEVER LE NUMERO DE TELEPHONE DANS LE INPUT QUAND ON VALIDE LA PREMIERE FOIS.
            <Form className="login-form eiga-form" onSubmit={this.onSubmit}>
                <div className="login-form-content">
                    {msgCatch}
                    <MyInput 
                        label={this.state.show === false ? "Connexion via Smarthpone" : "Entrer Votre Code"} 
                        id={this.state.show === false ? "phone": "codeVerification" }
                        type="tel"
                        placeholder={this.state.show === false ? "ex:+33610122325" : "Tapez le code de verification"}
                        error={this.state.show === false ? this.state.errors.phone : this.state.errors.codeVerification} 
                        maxLength={this.state.show === false? '12' : '6'}
                        value={this.state.show ?  this.state.values.phone : this.state.values.codeVerification}
                        changed={this.state.show === false ? (e) => this.handleInput(e, 1) : (e) => this.handleInput(e, 2)}/>
                    <p> {this.state.show === false ? 
                        'Entrer votre numero de mobile pour recevoir un code par SMS' :  
                        'Entrer le code reçu par SMS'}
                    </p>
                    <MyButton id="recaptcha-container" disabled={this.state.disable} clicked={this.state.show === false ? (e) => this.sendCode(e) : this.receiveCode}/>
                </div>
            </Form>
        )
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onPhone: (token, userId) => dispatch(actions.authSuccess(token, userId))
    };
};

export default withRouter(connect(null, mapDispatchToProps) (Phone));
