import React from 'react';
import Select from 'react-select';
import './Filtre.css';
import * as actions from '../../../../store/actions/index';
import { connect } from 'react-redux';

const optionsGenre = [
  { value: '28', label: 'Action' },
  { value: '16', label: 'Animation' },
  { value: '12', label: 'Aventure' },
  { value: '35', label: 'Comédie' },
  { value: '80', label: 'Crime' },
  { value: '99', label: 'Documentaire' },
  { value: '18', label: 'Drame' },
  { value: '10751', label: 'Familial' },
  { value: '14', label: 'Fantastique' },
  { value: '36', label: 'Histoire' },
  { value: '27', label: 'Horreur' },
  { value: '10402', label: 'Musique' },
  { value: '9648', label: 'Mystére' },
  { value: '10749', label: 'Romance' },
  { value: '878', label: 'Science-fiction' },
  { value: '10770', label: 'Téléfilm' },
  { value: '53', label: 'Thriller' },
  { value: '10752', label: 'Guerre' },
  { value: '37', label: 'Western' },
];

const optionsSortBy = [
  { value: 'popularity.desc', label: 'Popularité desc' },
  { value: 'popularity.asc', label: 'Popularité asc' },
  { value: 'vote_average.desc', label: 'Note desc' },
  { value: 'vote_average.asc', label: 'Note asc' },
];

const optionsYears = [];
  for (let i = 1960; i <= 2020; i++) 
    optionsYears.push({ value: i, label: i });

class Categrories extends React.Component {
  state ={
    filtres: {}
  }

  handleChange = (value, id) => {
    let filtres = {...this.state.filtres};
    filtres[id] = value;
    this.setState({filtres}, () => console.log('rrrrrr --> ' ,this.state))
  };

  // handleChangeGenre = selectedGenre => {
  //   this.props.changeGenre(selectedGenre);
  // };

  // handleChangeNote = selectedNote => {
  //     this.props.changeNote(selectedNote);
  // };

  // handleChangeYears = selectedYears => {
  //     this.props.changeYears(selectedYears);
  // };

  // handleChangeSortBy = selectedSortBy => {
  //     this.props.changeOrdre(selectedSortBy);
  // };

  render() {
    const customStyles=  {
      control: () => ({
        display:"flex",
        backgroundColor:"black",
        border: '1px solid white',
        marginRight:"15px",
        marginLeft:"15px",
        width:'200px',
        marginTop:"30px"
      }),
      placeholder: () => ({
        color: "white"
      }),
      option: (provided, state) => ({
        ...provided,
        display: 'flex',
        color: 'white',
        fontWeight: state.isSelected ? 'bold' : null,
        backgroundColor: state.isSelected ? 'grey' : 'black',
        width:'100%',
        '&:hover': {
          backgroundColor: 'red'
        }
      }),
      singleValue: () => ({
        color: 'white',
        widht:"100%"
      }),
    }

    return (
      <div className="Filtres">
        <div className="Filtres2">
          <Select
            styles={customStyles}
            value={this.state.filtres.genre}
            placeholder= "Genre"
            onChange={(value) => this.handleChange(value, "genre")}
            // onChange={this.handleChangeGenre}
            options={optionsGenre}/>
        </div>
        <div className="Filtres2">
          <Select
            styles={customStyles}
            value={this.state.filtres.genre}
            placeholder="Annee"
            onChange={this.handleChangeYears}
            options={optionsYears}/>
          <Select
            styles={customStyles}
            value={this.props.ordre}
            placeholder="Trier par"
            onChange={this.handleChangeSortBy}
            options={optionsSortBy}/>
          </div>
      </div>
    );
  }
}

// const mapStateToProps = state => {
