import React from 'react';
import Social from '../../containers/Auth/Login/Social/Social';
import Logo from '../Layout/Toolbar/Logo/Logo';

const Wrapper = (props) => (
    <div className="PageAuth">
        <div style={{width:'200px'}}>
            <Logo style={{top:'0', left:'0'}}/>

        </div>



        <div style={{width:'100%', margin:'auto'}}>
        {   props.title === 'Inscription' &&
            <>
                <h1 style={{fontFamily:'Helvetica Neue', fontSize:'50px', width:'600px', textAlign:'center', margin:'auto', marginBottom:'50px'}}>Films, séries TV et bien plus en illimité.</h1>
                <h2 style={{fontFamily:'Helvetica Neue',fontWeight:'400', fontSize:'1.2rem' ,width:'500px',textAlign:'center', margin:'auto',marginBottom:'50px'}}>Prêt à regarder Netflix ? Saisissez votre adresse e-mail et votre mot de passe pour vous inscrire.</h2>
            </>
        }
        <div className="Register" style={{margin:'auto'}}>
            <h1 className="login-form-title">{props.title}</h1>
            {props.form}
            {props.social && <Social />}        
        </div>  

        </div>

    </div> 
)

export default Wrapper
