import React, { Component } from "react";
import InfiniteScroll from "react-infinite-scroll-component";
import { connect } from "react-redux";
import "./MoviesList.css";
import MovieItems from "./MoviesItems/MoviesItems";
import * as actions from '../../../store/actions/index';

class MoviesList extends Component {
  state = {
    page: 1,
    hasMore: true,
  }

  componentDidMount () {
    console.log(this.props.movies.length);
    if (this.props.movies.length === 0)
      this.newMovie();
  }

  //IL FAUT REINITIALISER LESFILTRES QUAND TEXTSEARCH ET INVERSE.

  componentDidUpdate (prevProps) {
    if (prevProps.filtres !== this.props.filtres) {
      this.setState((prevState) => ({page: prev}))
      this.props.initialiseMovies();
      this.props.initialisePage();
      this.props.filtresRequest(this.props.filtres, this.props.page + 1);
    }
    else if (prevProps.textSearch !== this.props.textSearch) {
      this.props.initialiseMovies();
      this.props.initialisePage();
      this.props.textSearchRequest(this.props.textSearch, this.props.page + 1);
    } 
  }

  newMovie = () => {
    if (this.props.nbPage !== 0 && this.state.page > this.props.nbPage) {
      this.setState({ hasMore: false });
    }
    // else if (this.props.next === 'popular') {
    //   this.props.popularRequest(this.props.page + 1);
    // }
    else if (this.props.next === 'filtres') {
      this.props.filtresRequest(this.props.filtres, this.props.page + 1);
    }
    else if (this.props.next === 'textSearch') {
      this.props.textSearchRequest(this.props.textSearch, this.props.page + 1);
    }
    // popular aussi a simplifier
    else {
      this.props.popularRequest(this.props.page + 1);
    }
  };

  render() {
    let movies = null;
    if (this.props.movies !== null && this.props.movies.length > 0) 
      movies = <MovieItems movies={this.props.movies} clicked={() => this.clicked()}/>
    if (this.props.movies.length === 0)
      movies = <p style={{ color: "white" }}>Il y a 0 resultat.</p>

    //CREER UNE VARIABLE DANS LE STORE ET L'UTILISER POUR FAIRE DES IF ELSE IF ET SAVOIR QUE METTRE DANS NEXT EN FONCTION DES FITLRES.
    return (
      <div className="block">
        <InfiniteScroll
          dataLength={this.props.movies.length}
          next={this.newMovie}
          hasMore={this.state.hasMore}
          loader={<h4>Loading...</h4>}
          endMessage={<p style={{ textAlign: "center" }}><b>Yay! Vous avez tout vu</b></p>}>
          {movies}
        </InfiniteScroll>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    page: state.filtres.page,
    nbPage: state.filtres.nbPage,
    filtres: state.filtres.filtres,
    textSearch: state.filtres.textSearch,
    movies: state.filtres.movies,
    next: state.filtres.next
  };
};

const mapDispatchToProps = dispatch => {
  return {
    initialisePage: () => dispatch(actions.initialisePage()),
    initialiseMovies: () => dispatch(actions.initialiseMovies()),
    popularRequest: (page) => dispatch(actions.popularRequest(page)),
    filtresRequest: (filtres, page) => dispatch(actions.filtresRequest(filtres, page)),
    textSearchRequest: (textSearch, page)=> dispatch(actions.textSearchRequest(textSearch, page))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(MoviesList);
