import React, { Component } from 'react';

import { NavLink } from 'react-router-dom';
// import { connect } from 'react-redux';

import { checkInput } from "../../../components/utility/utility";
// import * as actions from '../../store/actions/index';

import MyInput from "../../../components/MyInput/MyInput";
import MyButton from '../../../components/MyButton/MyButton';
import Social from '../Social';

import { Form } from 'react-bootstrap';
import '../auth.css';

class Login extends Component {
    state = {
        usersData: null,
        values: {},
        errors: {},
        disabled: true,
        form: [
            {
              controlId: "mail",
              label: "Email",
              type: "text",
              min: 4,
              max: 40
            },
            {
              controlId: "password",
              label: "Mot de passe",
              type: "password",
              min: 6,
              max: 15
            }
        ]
    };

    componentDidMount() {
        console.log("errors -> ", this.state.errors);
    }

    changedHandler = (e, min, max) => {
        const Id = e.target.id;
        const values = {...this.state.values};
        values[Id] = e.target.value;
        this.setState({values : values}, () => {
            let error = null;
            error = checkInput(Id, values[Id], min, max);
            const errors = {...this.state.errors};
            errors[Id] = error;
            this.setState({errors: errors}, () => {Object.values(this.state.errors).join('').length === 0 && Object.keys(this.state.errors).length === 2 ? this.setState({disabled: false}) : this.setState({disabled: true});})
        });
    };

    submit = (e) => {
        e.preventDefault();
        console.log('OK');
        this.props.authLog(this.state.values.mail, this.state.values.password,this.props.history) 
    };

    render () {
        const form = (
            <Form className="login-form eiga-form" onSubmit={this.submit}>
                <div className="login-form-content">
                    {this.state.form.map(input => (
                        <MyInput
                            data-test={input.label}
                            key={input.label}
                            controlId={input.controlId}
                            type={input.type}
                            label={input.label}
                            // value={this.state.value[input.controlId]}
                            error={this.state.errors[input.controlId]}
                            changed={(e) => this.changedHandler(e, input.min, input.max)}
                            />
                    ))}
                    <Form.Group className="Links">
                        <Form.Text>
                            Pas encore membre ? <NavLink className="Link" to="/">Par ici !</NavLink>
                        </Form.Text>
                        <Form.Text>
                            <NavLink className="Link" to="/forget_password">Mot de passe oublié ?</NavLink>
                        </Form.Text>
                    </Form.Group>
                    <MyButton data-test="button" disabled={this.state.disabled} />
                </div>
            </Form>
        )

        return (
            <div className="Page">
                <div className="Register">
                    <div className="Login-wrapper">
                        <h1 className="login-form-title">Connexion</h1>
                        {form}
                        <Social/>
                    </div>
                </div>
            </div>
        );
    };
};

const mapStateToProps = state => {
    return {
      isAuth: state.auth.token != null
    };
};

const mapDispatchToProps = dispatch => {
    return {
        authLog: (email, password, router) => dispatch(actions.authLog(email, password,router))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
