import React from 'react';
import { useSelector } from 'react-redux';
import './ShoppingCart.css';
import { AiOutlineCloseCircle } from 'react-icons/ai'

const ShoppingCart = () => {
    const movies = useSelector(state => state.movies.movies);
    const slides = 

    <ul>
        {movies.map(movie => (
            <li className="BlockFilm" style={{color:'yellow'}} key={movie.id}>
            <img style={{width:'150px', height:'200px', backgroundColor:'green'}} src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`} alt={movie.id} key={movie.id}/>

                <div className="InfoFilm">
                    <p>{movie.title}</p>
                    <p>10$</p>
                </div>
                <div>
                    <AiOutlineCloseCircle/>
                </div>
            </li>
                ))}

    </ul>

    return (
        <div className="PageCart">
            <div className="BlockCart">
                <h1 className='Title'>Mon Panier</h1>
                {slides}

            </div>
        </div>
    )
}

export default ShoppingCart;
