import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../utility';

const initialState = {
  movies: [],
  genre: { value: '', label: 'Genre'},
  years: { value: '', label: 'Annee'}, 
  ordre: { value: 'popularity.desc', label: 'Popularité desc' },
  textSearch : null
};


  const movies = (state, action) => {
    return updateObject( state, { movies: action.movies } );};

const reducer = ( state = initialState, action ) => {
  switch ( action.type ) {
    case actionTypes.GENRE: return genre(state, action);
    case actionTypes.ORDRE: return ordre(state, action);
    case actionTypes.YEARS: return years(state, action);
    case actionTypes.TEXT_SEARCH: return textSearch(state, action);

    case actionTypes.MOVIES: return movies(state, action);
    default: return state;
  }
}; 

export default reducer;
