import * as actionTypes from './actionTypes';
import axios from 'axios';

export const genre = (genre) => {
    return {
        type: actionTypes.GENRE,
        genre: genre
    };
};

export const ordre = (ordre) => {
    return {
        type: actionTypes.ORDRE,
        ordre: ordre
    };
};

export const years = (years) => {
    return {
        type: actionTypes.YEARS,
        years: years
    }; 
};

// export const textSearch = (textSearch) => {
//     return {
//         type: actionTypes.TEXT_SEARCH,
//         textSearch: textSearch
//     }; 
// };

export const moviesFiltres = (movies) => {
    return {
        type: actionTypes.MOVIES_FILTRES,
        movies: movies
    }
};


export const filtres = () => {
    return dispatch => {
        axios.get('https://api.themoviedb.org/3/discover/movie?api_key=1e32f5c452c2267d5367589e9864ab1c&with_genres=28')
            .then(response => {
                dispatch(moviesFiltres(response.data.result));
            })
            .catch(err => console.log(err))
    }
}

export const textSearch = (textSearch) => {

    return dispatch => {
        axios.get(`${PATH_BASE}${PATH_SEARCH}${PATH_MOVIE}${API_KEY}${PATH_PAGE}${this.state.page}${PATH_LANGUE}fr${PATH_ADULT}"&query='sonic'`)
        .then(response => {
            dispatch(moviesFiltres(response.data.result));
        })
        .catch(err => console.log(err))
    }
}
