import React, { Component } from 'react';
import Img from '../../../assets/Images/miclaude.png';
import { Container, Image, Button } from 'react-bootstrap';
// import axios from 'axios';

class Photo extends Component {
    state = {
        avatar: null
    };

    handleChange = (e) => {
        this.setState({avatar: e.target.files[0]}, () => {
            const fd = new FormData();
            fd.append('image', this.state.avatar, this.state.avatar.name);
            // axios.post('https://movies-52928.firebaseio.com/images.json', fd)
            //     .then(res => console.log(res))
            //     .catch(err => console.log(err));  
        } )
    };

    render () {
        // let avatar = <Image src={Img} className='p-3' style={{width:'200px', height:'200px'}} roundedCircle />;
        let avatar = null
        if (this.state.pictures > 0)
            avatar = <Image src={this.state.avatar} className='Avatar' roundedCircle />;
        return ( 
            <Container className='d-flex flex-column justify-content-center align-items-center'>
                {avatar}
                <Button variant="secondary" style={{width:'200px'}} onClick={() => this.fileInput.click()}>Modifier avatar</Button>
                <input style={{display: 'none'}} type="file" onChange={this.handleChange}
                    ref={fileInput => this.fileInput = fileInput}/>
            </Container>
        );
    };
};

export default Photo;
