const checkRegex = (id, value) => {
    let regex = null;
    let errors = null;

    if (id === 'mail') {
        regex = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
        if (!regex.test(value))
            errors = '* Adresse Mail incorect';
    }
    else if (id === 'password') {
        regex = /^(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9]).{6,}/;
        if (!regex.test(value))
            errors = '* Au moins une minuscule, une majuscule et un chiffre';
    }
    return errors;
}

export const checkInput = (id, value,min, max) => {
    let error = null;

    if (value.length < min)
        error = `* ${min} caractères min.`;
    else if (value.length > max)
        error = `* ${max} caractères max.`;
    else
        error = checkRegex(id, value);
    return error;
}
