import React, { Component } from "react";
import { checkInput } from "../../../components/utility/utility";
// import Social from './Social';
import { Form } from "react-bootstrap";
import  '../auth.css';

// import { NavLink } from 'react-router-dom';

import MyInput from "../../../components/MyInput/MyInput";
import MyButton from "../../../components/MyButton/MyButton";
// import Avatar from '../../../components/Avatar/Avatar';

// import PropTypes from 'prop-types';

class Register extends Component {
  state = { 
    values: {},
    errors: {},
    disabled: true,
    avatar: null, 

    form: [
      {
        controlId: "mail",
        label: "Email",
        type: "text",
        min: 4,
        max: 40
      },
      {
        controlId: "password",
        label: "Mot de passe",
        type: "password",
        min: 6,
        max: 15
      }
    ]
  };

  changedHandler = (e, min, max) => {
    console.log("[this.state.errors]  ", this.state.errors);
    const id = e.target.id;
    const values = { ...this.state.values };
    values[id] = e.target.value;

    this.setState({ values: values }, () => {
      let disabled = Object.keys(this.props.errors || {}).length;
      let error = checkInput(id, values[id], min, max);
      const errors = { ...this.state.errors };
      delete errors[id];
      if (error) {
        errors[id] = error; 
        disabled = true;
      }
      this.setState({ errors });
      this.setState({ disabled });
    });
  };

  changedHandler = (e, min, max) => {
    const Id = e.target.id;
    const values = { ...this.state.values };
    values[Id] = e.target.value;

    this.setState({ values: values }, () => {
      let error = null;
      error = checkInput(Id, values[Id], min, max);
      const errors = { ...this.state.errors };
      errors[Id] = error; 
      this.setState({ errors: errors },
        () => { Object.values(this.state.errors).join('').length === 0 && Object.keys(this.state.errors).length === 2 ? this.setState({ disable: false }) : this.setState({ disable: true }); })
    });
};

  submit = e => {
    e.preventDefault();
    this.props.onSubmit();
  };

  render() {
    const form = (
      <Form className="login-form eiga-form" onSubmit={this.submit}>
        <div className="login-form-content">
          {this.state.form.map(input => (
            <MyInput
              data-test={input.label}
              key={input.label}
              controlId={input.controlId}
              type={input.type}
              label={input.label}
              // value={this.state.value[input.controlId]}
              error={this.state.errors[input.controlId]}
              changed={(e) => this.changedHandler(e, input.min, input.max)}
            />
          ))}
            {/* <Avatar /> */}
            <Form.Text className="mt-3">
                {/* Deja membre ?<NavLink className='Link' to="/connexion"> Connectez-vous</NavLink> */}
            </Form.Text>
            <MyButton data-test="button" disabled={this.state.disabled} />
        </div>
      </Form>
    );

    return (
      <div className="Page">
        <div className="Register">
          <h1 className="login-form-title">Inscription</h1>
          {form}
        </div>
      </div>
    );
  }
}

export default Register;
