import React, { useEffect, useState } from 'react';
import { Carousel } from 'react-bootstrap';
import axios from 'axios';

import Slider from 'react-slick';
import { Image } from 'react-bootstrap';

const MyOrder = () => {

    var settings = {
        width: '90%',
        dots: true,
        infinite: true,
        slidesToShow: 4,
        slidesToScroll: 4,
        autoplay: true,
        autoplaySpeed: 4000,
        speed: 1000,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
            }
          },
          {
            breakpoint: 780,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
              initialSlide: 2,
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
            }
          }
        ]
      };


    const [orderUser, setOrderUser] = useState([]);
    useEffect(() => {
        axios.get(`https://movies-27cd5.firebaseio.com/${localStorage.getItem('id')}/Order.json/`)
        .then(res => {
            console.log(res.data)
            if (res.data !== null) 
                setOrderUser(res.data)
        })
        .catch(err => {})   
    }, [])

    let order = orderUser.map(order=> {

        return (
          <>
            <h4 className='h4'>Commande {order.numberOrder} du {order.date}</h4>
            <Slider {...settings}>
                  {
                  order.films.map(movie => {
                      return (
                        <div key={movie.id} className='blockImage' >
                          <Image className="Image" src={`https://image.tmdb.org/t/p/w500/${movie.poster_path || movie.img}`} alt={movie.title} />
                        </div>
                      )
                    })
                  }
            </Slider>

          </>







            )
        }
    )

    return (<>{order}</>)
}

export default MyOrder;
