    //        
    //     }, [location])
    // }



    const clicked = (e) => {
        setColor1('LiProfil');
        setColor2('LiProfil');
        setColor3('LiProfil');

        if (pathname === '/profil/')
            setColor1('LiClick');
        else if (pathname === '/profil/MyOrder')
            setColor2('LiClick');


        // if (e.target.id === '1') 
        //   
        // if (e.target.id === '2')
