import React from 'react';
import { useSelector } from 'react-redux';
import './ShoppingCart.css';
import { AiOutlineCloseCircle } from 'react-icons/ai'

const ShoppingCart = () => {
    const movies = useSelector(state => state.movies.movies);
    const slides = movies.map(movie => (
        <div className="flip-card">
            <div className="flip-card-inner">
                <div className="flip-card-front">
                    <img src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`} className='ImgMyList' alt={movie.id} key={movie.id}/>
                </div>
                <div className="flip-card-back">
                    <h1>{movie.title}</h1>
                </div>
            </div>
        </div>
    ));
    return (
        <div className="PageCart">
            <div className="BlockCart">
                <h1 className='Title'>Mon Panier</h1>
                <div className="BlockFilm" style={{color:'yellow'}}>
                    <div style={{width:'150px', height:'250px', backgroundColor:'green'}}></div>
                    <div className="InfoFilm">
                        <p>The avengers</p>
                        <p>10$</p>
                    </div>
                    <div>
                    <AiOutlineCloseCircle/>
                    </div>
                </div>

            </div>
        </div>
    )
}

export default ShoppingCart;
