import React, { Component } from "react";
import axios from "axios";
import InfiniteScroll from "react-infinite-scroll-component";
import { connect } from "react-redux";
import "./MoviesList.css";
import { withRouter } from 'react-router-dom';
import MovieItems from "./MoviesItems/MoviesItems";
import * as actions from '../../../store/actions/index';

import { API_KEY, PATH_BASE, PATH_DISCOVER, PATH_MOVIE, PATH_SEARCH, PATH_PAGE, PATH_ADULT, PATH_LANGUE } from  './api';

class MoviesList extends Component {
  state = {
    movies: [],
    page: 1,
    hasMore: true,
    nbPage: 0
  };

  componentDidMount () {
    console.log('didmount',this.props.textSearch)
    if (this.props.textSearch !== null)
      this.searchMovies();
  }

  componentDidUpdate(prevProps) {
    if (prevProps.textSearch !== this.props.textSearch) 
     this.searchMovies();
    else if (prevProps.genre !== this.props.genre || prevProps.years !== this.props.years || prevProps.note !== this.props.note || prevProps.ordre !== this.props.ordre)
      this.filterMovies();
  };

  searchMovies = () => {
      axios.get(`${PATH_BASE}${PATH_SEARCH}${PATH_MOVIE}${API_KEY}${PATH_PAGE}${this.state.page}${PATH_LANGUE}fr${PATH_ADULT}"&query=${this.props.textSearch}`)
      .then(response => {
        this.setState({movies : response.data.results});
      })
  };

  filterMovies = () => {
    let url = `${PATH_BASE}${PATH_DISCOVER}${PATH_MOVIE}${API_KEY}${PATH_PAGE}${this.state.page}${PATH_ADULT}${PATH_LANGUE}fr`;
      if (!!this.props.genre) 
        url += `&with_genres=${this.props.genre}`;
      if (!!this.props.years)
        url += `&primary_release_year=${this.props.years}`;
      if (!!this.props.ordre)
        url += `&sort_by=${this.props.ordre}`;


      axios.get(url)
      .then(response => {
        this.setState({movies: response.data.results})
      })
  }



  render() {
    let movies = null;
    if (this.state.movies !== null && this.state.movies.length > 0) 
      movies = <MovieItems movies={this.state.movies} clicked={() => this.clicked()}/>
    if (this.state.movies.length === 0)
      movies = <p style={{ color: "white" }}>Il y a 0 resultat pour: "{this.props.textSearch}".</p>

    let asyncPagination = null;

    return (
      <div className="block">
        <InfiniteScroll
          dataLength={this.state.movies.length}
          next={asyncPagination}
          hasMore={this.state.hasMore}
          loader={<h4>Loading...</h4>}
          endMessage={<p style={{ textAlign: "center" }}><b>Yay! You have seen it all</b></p>}>
          {movies}
        </InfiniteScroll>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    genre: state.filtres.genre.value,
    years: state.filtres.years.value,
    ordre: state.filtres.ordre.value,
    textSearch: state.filtres.textSearch
  };
};

const mapDispatchToProps = dispatch => {
  return {

    changeGenre: (genre) => dispatch(actions.genre(genre)),
    changeYears: (years) => dispatch(actions.years(years)),
    changeOrdre: (ordre) => dispatch(actions.ordre(ordre)),
    actionTextSearch: () => dispatch(actions.textSearch(null))
  };
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(MoviesList));
