import React, { Component } from 'react';
import Sidebar from './Sidebar/SideBar';
import MoviesList from './MoviesList/MoviesList';
import './Home.css';

import { connect } from 'react-redux';
import * as actions from '../../store/actions/index'; 

class Home extends Component {
    state = {
        inputValue: '',
        search: null
    };

    handleSearchChange = (e) => {
        this.props.actionTextSearch(e.target.value);
        // this.setState({inputValue: e.target.value}, () => {});
    };

    render () {
        return (
            <div className="Page">
                <div className="Gauche">
                    <Sidebar changed={this.handleSearchChange} clicked={this.clicked}/>
                </div>
                <div className="Droite">
                    <MoviesList/>
                </div>
            </div>
        );
     };
};

const mapStateToProps = state => {
    return {
        textSearch : state.filtres.textSearch
    };
};

const mapDispatchToProps = dispatch => {
    return {
        actionTextSearch: (textSearch) => dispatch(actions.textSearch(textSearch)),
    };
};

export default connect(mapStateToProps, mapDispatchToProps) (Home);
