import React, { Component } from 'react'
import { Form } from 'react-bootstrap'
import firebase from '../../../../fire';
import * as regex from './RegexPhone'
import { withRouter } from 'react-router-dom';
import * as actions from '../../../../store/actions/index';
import { connect } from 'react-redux';
import MyInput from '../../../../components/MyInput/MyInput';
import MyButton from '../../../../components/MyButton/MyButton';
import '../../auth.css';

class Phone extends Component {
    state = {
        phone:null,
        codeVerification:null,
        error:{},
        errorCatch:'',
        disable: true,
        show: false,
        codeError:null
    }

    //// REVOIR LA PARTIEEEE HANDLER ET LES 3 METHODE POUR EN FAIRE QU'UNE COMME POUR REGISTER
    handleFormValid = (name_input) => { 
        let number = null;
        let store = ''; 
        name_input === 'phone' ? number = 1 : number = 2;
        const error = {...this.state.error};
        const values = Object.values(error);

        for (const key of values)
            store += key;
        Object.keys(error).length === number && store.length === 0 ? 
        this.setState({disable:false}) : this.setState({disable:true},()=>{console.log(this.state.disable1)});
    }

    handleInputValid = (name_input,value_input) => {
        let error = {...this.state.error};

        //FAIRE REGEX COMME POUR REGISTER ET LE RESTE.

        // error[name_input] = checkInput(name_input, value_input);


        switch(name_input){
            case 'phone': value_input.match(regex.phoneNumber) ? error[name_input]='' : error[name_input] = "* Format: +33610122334";
                break;
            case 'codeVerification':value_input.match(regex.codeVerification) && value_input.length === 6 ? error[name_input]='' : error[name_input] = "* Code incorrect ex: 123456";
                 break;
            default:
                console.log("NUMBER NOT FOUND");
        }
        this.setState({ error : error }, () => {this.handleFormValid(name_input)});
    }

    handleInput = (event) => {
        const nameInput = event.target.id;
        console.log('777777',nameInput)
        const valueInput = event.target.value;
        this.setState({[event.target.id]:event.target.value},
            () => {this.handleInputValid(nameInput,valueInput)    
        });
    }

    changedHandler = (e, min, max) => {
        const Id = e.target.id;
        console.log('frfrfrfrfrfr', Id);
        const values = {...this.state.values};
        values[Id] = e.target.value;
        this.setState({values : values}, () => {
          let error = null;
          error = checkInput(Id, values[Id], min, max);
          const errors = {...this.state.errors};
          errors[Id] = error;
          this.setState({errors: errors}, () => { 
            Object.values(this.state.errors).join('').length === 0 && Object.keys(this.state.errors).length === 2 ? 
            this.setState({ disabled: false }) : this.setState({ disabled: true });
          })
        });
      };

    sendCode = (e) => {  
        e.preventDefault();
        firebase.auth().languageCode = 'fr';
        window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier("recaptcha-container",
        {
           size:"invisible"
        });
        let phoneNumber = this.state.phone;
        let appVerifier = window.recaptchaVerifier;
        firebase.auth().signInWithPhoneNumber(phoneNumber, appVerifier)
        .then(confirmationResult => { 
            this.setState({ show: true, disable : true }, () => {
                window.confirmationResult = confirmationResult;
            })
        })
        .catch(error => {
            this.setState({errorCatch:error.message})
        });
    }

    receiveCode= (e) => {
        e.preventDefault();
        const verificationId = this.state.codeVerification;
        window.confirmationResult
        .confirm(verificationId)
        .then(result => {
            localStorage.setItem('id',result.user.uid)
            localStorage.setItem('token', result.user.ma)
            localStorage.setItem('show', true)
            this.props.onPhone(result.user.ma,result.user.uid);
            this.props.history.push('/home')
        })
        .catch(error => {
            this.setState({codeError:'Error'});
        });
    }

    render() {
        let msgCatch;

        if (this.state.errorCatch && this.state.codeError === null)
            msgCatch = <p>Trop d'essai votre compte a Ã©tÃ© temporairement suspendu. Reesayer plus tard</p>;

        if (this.state.codeError)
            msgCatch = <p>le code ne coresspond pas a celui qui vous a Ã©tÃ© envoyer veuillez ressayer</p>;

        return (
            //IL FAUT ENLEVER LE NUMERO DE TELEPHONE DANS LE INPUT QUAND ON VALIDE LA PREMIERE FOIS.
            <Form className="login-form eiga-form" onSubmit={this.onSubmit}>
                <div className="login-form-content">
                    {msgCatch}
                    <MyInput 
                        label={this.state.show === false ? "Connexion via Smarthpone" : "Entrer Votre Code"} 
                        id={this.state.show === false ? "phone": "codeVerification" }
                        type="tel"
                        placeholder={this.state.show === false ? "ex:+33610122325" : "Tapez le code de verification"}
                        error={this.state.show === false ? this.state.error.phone : this.state.error.codeVerification} 
                        changed={this.handleInput}/>
                    <p> {this.state.show === false ? 
                        'Entrer votre numero de mobile pour recevoir un code par SMS' : 
                        'Entrer le code reÃ§u par SMS'}
                    </p>
                    <MyButton id="recaptcha-container" disabled={this.state.disable} clicked={this.state.show === false ? this.sendCode : this.receiveCode}/>
                </div>
            </Form>
        )
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onPhone: (token, userId) => dispatch(actions.authSuccess(token, userId))
    };
};

export default withRouter(connect(null, mapDispatchToProps) (Phone));
