import React from 'react';

const ConfirmOrder = () => {
    let order = 'FUWTS' + localStorage.getItem('numOrder');
    return (
    <div style={{height:'100vh', width:'65%', margin:'auto', marginTop:'50px'}}>
        <h1>Merci, nous avons reçu votre commande nᵒ {order}</h1>

        <p style={{marginTop:'50px'}}>Veuilez consulter votre messagerie pour obtenir la confirmation de commande, ainsi que tout les details correspond a votre achat.
        <p>Vous avez dorenavant la possibilite d'acceder a vos films dans votre espace perso.</p></p>

        Facture livrer au (adresse client).
    </div>
    )
}
export default ConfirmOrder;
