// import Photo from './Photo/Photo';
import React, { Component } from 'react';
import { Form, Button } from 'react-bootstrap';
import { checkInput } from '../../components/utility/utility';
import './Profil.css';

class Register extends Component {
    state = {
        values: {},
        errors: {},
        disable: true,
        avatar: null,

        form: [
            {
                controlId: "lastName",
                label: "Nom",
                type: "text",
                min: 2,
                max: 20
            },
            {
                controlId: "firstName",
                label: "Prenom",
                type: "text",
                min: 2,
                max: 20
            },
            {
                controlId: "username",
                label: "Prenom",
                type: "text",
                min: 2,
                max: 20
            },
            {
                controlId: "mail",
                label: "Email",
                type: "text",
                min: 4,
                max: 40
            },
            {
                controlId: "password",
                label: "Mot de passe",
                type: "password",
                min: 6,
                max: 15
            },

        ]
    };

    changedHandler = (e, min, max) => {
        const Id = e.target.id;
        const values = { ...this.state.values };
        values[Id] = e.target.value;

        this.setState({ values: values }, () => {
          let error = null;
          error = checkInput(Id, values[Id], min, max);
          const errors = { ...this.state.errors };
          errors[Id] = error; 
          this.setState({ errors: errors },
            () => { Object.values(this.state.errors).join('').length === 0 && Object.keys(this.state.errors).length === 5 ? this.setState({ disable: false }) : this.setState({ disable: true }); })
        });
    };

    submit = (e) => {
        e.preventDefault();
        console.log('OK');
    };

    render () {
        return (
            <div className='Page'>
                <div className="Register">
                    <Form className="login-form eiga-form" onSubmit={this.submit}>
                        <div className="login-form-content">
                            <h1 className='H1'>Mon Profil</h1>
                            <h3 className='H2'>Modifier mes informations</h3>
                            <Form.Group>
                                <Form.Label>Avatar</Form.Label>
                                {/* <Photo /> */}
                                <input style={{display: "none"}}type="file" onChange={this.handleChange} ref={fileInput => this.fileInput = fileInput}/>
                                <Form.Text className="text-muted">
                                    {this.state.errors.avatar}
                                </Form.Text>
                            </Form.Group>

                            <Form.Group controlId="lastName" >
                                <Form.Label>Nom</Form.Label>
                                <Form.Control type="text" placeholder="Nom" size="m" onChange={(e) => this.changedHandler(e, null, 40)}/>
                                <Form.Text className="text-muted">
                                    {this.state.errors.lastName}
                                </Form.Text>
                            </Form.Group>

                            <Form.Group controlId="firstName" >
                                <Form.Label>Prenom</Form.Label>
                                <Form.Control type="text" placeholder="Prenom" size="m" onChange={(e) => this.changedHandler(e, null, 40)}/>
                                <Form.Text className="text-muted">
                                    {this.state.errors.firstName}
                                </Form.Text>
                            </Form.Group>

                            <Form.Group controlId="username" >
                                <Form.Label>Login</Form.Label>
                                <Form.Control type="text" placeholder="Login" size="m" onChange={(e) => this.changedHandler(e, null, 40)}/>
                                <Form.Text className="text-muted">
                                    {this.state.errors.username}
                                </Form.Text>
                            </Form.Group>

                            <Form.Group controlId="mail" >
                                <Form.Label>Email</Form.Label>
                                <Form.Control type="email" placeholder="Email"  onChange={(e) => this.changedHandler(e, null, 40)}/>
                                <Form.Text className="text-muted">
                                    {this.state.errors.mail}
                                </Form.Text>
                            </Form.Group>

                            <Form.Group controlId="password">
                                <Form.Label>Mot de passe</Form.Label>
                                <Form.Control type="password" placeholder="Mot de passe" size="m" onChange={(e) => this.changedHandler(e, 6, 15)}/>
                                <Form.Text className="text-muted">
                                    {this.state.errors.password}
                                </Form.Text>
                            </Form.Group>


                            <Button variant="danger" disabled={this.state.disable} type="submit" className="button login-form-submit">Valider</Button>
                        </div>
                    </Form>
                </div>
            </div>
        );
    };
};


export default Register;
