import React, { Component } from "react";
import InfiniteScroll from "react-infinite-scroll-component";
import { connect } from "react-redux";
import "./MoviesList.css";
import MovieItems from "./MoviesItems/MoviesItems";
import * as actions from '../../../store/actions/index';

import axios from 'axios';

class MoviesList extends Component {

  state = {
    movies: [],
    page: 1,
    hasMore: true,
    nbPage: 0
  }
  componentDidMount () {
    this.newMovie();
  }

  newMovie = () => {
    if (this.state.nbPage !== 0 && this.state.page > this.state.nbPage) {
      this.setState({ hasMore: false });
      return;
    }
    //action requete.
    //recuperer nbPage
    // this.props.popular(this);
    axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=1e32f5c452c2267d5367589e9864ab1c&language=fr&page=${this.state.page}`)
      .then(response => {
        this.setState({ nbPage: response.data.total_pages }, () => {
        console.log(response)
          this.setState(prevState => ({movies: [...this.state.movies, ...response.data.results], page: prevState.page + 1}));
        });
      })
      .catch(err => { console.log(err)})
  };

  render() {
    let movies = null;
    if (this.state.movies !== null && this.state.movies.length > 0) 
      movies = <MovieItems movies={this.state.movies} clicked={() => this.clicked()}/>
    if (this.state.movies.length === 0)
      movies = <p style={{ color: "white" }}>Il y a 0 resultat.</p>

    return (
      <div className="block">
        <InfiniteScroll
          dataLength={this.state.movies.length}
          // next={this.props.popular(this.props.page)}
          next={this.newMovie}
          hasMore={this.state.hasMore}
          loader={<h4>Loading...</h4>}
          endMessage={<p style={{ textAlign: "center" }}><b>Yay! Vous avez tout vu</b></p>}>
          {movies}
        </InfiniteScroll>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    page: state.filtres.page,
    hasMore: state.filtres.hasMore,
    movies: state.filtres.movies,
    nbPage: state.filtres.nbPage
  };
};

const mapDispatchToProps = dispatch => {
  return {
    popular: (page) => dispatch(actions.popular(page))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(MoviesList);
