import React from 'react';
import Sidebar from './Sidebar/SideBar';
import MoviesList from './MoviesList/MoviesList';
import './Home.css';
import Animation from './Animation/Animation';
import { useEffect } from 'react';

const Home = () =>  {
    const [animation, setAnimation] = useState(localStorage.getItem('animation'));
    useEffect(() => {
        console.log(localStorage.getItem('animation'))
        if (localStorage.getItem('animation')) {
            setTimeout(() => {
                localStorage.setItem('animation', null)
            }, 5000)
        }
    }, [localStorage]);

    const Page = (
        <div className="Page">
            <div className="Gauche">
                <Sidebar/>
            </div>
             <div className="Droite">
                <MoviesList/>
            </div>
        </div>
    )
    let display = null;
    if (localStorage.getItem('animation'))
        display = <Animation />
    else if (localStorage.getItem('animation')) 
        display = Page

    return (
        <> 
            {display}
        </>
    )   
}

export default Home;
