import React, { Component } from 'react';

import { NavLink } from 'react-router-dom';
// import { connect } from 'react-redux';

import { checkInput } from "../../../components/utility/utility";
// import * as actions from '../../store/actions/index';
// import Social from './Social';
import MyInput from "../../../components/MyInput/MyInput";
import MyButton from '../../../components/MyButton/MyButton'

import { Form, Button } from 'react-bootstrap';
import '../auth.css';

class Login extends Component {
    state = {
        usersData: null,
        values: {},
        errors: {},
        disable: true,

        form: [
            {
              controlId: "mail",
              label: "Email",
              type: "text",
              min: 4,
              max: 40
            },
            {
              controlId: "password",
              label: "Mot de passe",
              type: "password",
              min: 6,
              max: 15
            }
          ]
      };

    changedHandler = (e, min, max) => {
        const Id = e.target.id;
        const values = {...this.state.values};
        values[Id] = e.target.value;
        this.setState({values : values}, () => {
            let error = null;
            error = checkInput(Id, values[Id], min, max);
            const errors = {...this.state.errors};
            errors[Id] = error;
            this.setState({errors: errors}, () => {Object.values(this.state.errors).join('').length === 0 && Object.keys(this.state.errors).length === 2 ? this.setState({disable: false}) : this.setState({disable: true});})
        });
    };

    submit = (e) => {
        e.preventDefault();
        console.log('OK');
        // this.props.onAuth(this.state.values.mail, this.state.values.password);
    };

    render () {
        const form = (
            <Form className="login-form eiga-form" onSubmit={this.submit}>
                <div className="login-form-content">
                    {this.state.form.map(input => (
                        <MyInput
                        data-test={input.label}
                        key={input.label}
                        controlId={input.controlId}
                        type={input.type}
                        label={input.label}
                        // value={this.state.value[input.controlId]}
                        error={this.state.errors[input.controlId]}
                        changed={(e) => this.changedHandler(e, input.min, input.max)}
                        />
                    ))}

                    <Form.Group className="Links">
                        <Form.Text>
                            Pas encore membre ? <NavLink className="Links" to="/">Par ici !</NavLink>
                        </Form.Text>
                        <Form.Text>
                            <NavLink className="Links" to="/forget_password">Mot de passe oubli√© ?</NavLink>
                        </Form.Text>
                    </Form.Group>

                    <MyButton data-test="button" disabled={this.state.disabled} />
                </div>
            </Form>
        )
        return (
            <div className="Page">
                <div className="Register">
                    <div className="Login-wrapper">
                        <h1 className="login-form-title">Connexion</h1>
                        {form}
                        {/* <Social/> */}
                    </div>
                </div>
            </div>
        );
    };
};

// const mapDispatchToProps = dispatch => {
//     return {
//         onAuth: (email, password) => dispatch(actions.auth(email, password))
//     }
// }

// export default connect(null, mapDispatchToProps)(Login);

export default Login;
