import React, { Component } from "react";
import { checkInput } from "../../../components/utility/utility";
// import Social from './Social';
import { Form } from "react-bootstrap";
import  '../auth.css';

import { NavLink } from 'react-router-dom';

import MyInput from "../../../components/MyInput/MyInput";
import MyButton from "../../../components/MyButton/MyButton";
// import Avatar from '../../../components/Avatar/Avatar';

import PropTypes from 'prop-types';

class Register extends Component {
  state = {
    values: {},
    errors: {},
    disable: true,
    avatar: null, 

    form: [
      {
        controlId: "lastName",
        label: "Nom",
        type: "text",
        min: 2,
        max: 15
      },
      {
        controlId: "firstName",
        label: "Prenom",
        type: "text",
        min: 2,
        max: 15
      },
      {
        controlId: "username",
        label: "Login",
        type: "text",
        min: 2,
        max: 15
      },
      {
        controlId: "mail",
        label: "Email",
        type: "text",
        min: null,
        max: 40
      },
      {
        controlId: "password",
        label: "Mot de passe",
        type: "password",
        min: 6,
        max: 15
      }
    ]
  };

  changedHandler = (e, min, max) => {
    console.log("[this.state.errors]  ", this.state.errors);
    const id = e.target.id;
    const values = { ...this.state.values };
    values[id] = e.target.value;

    this.setState({ values: values }, () => {
      // on verifie si il y a des errors, si des erreurs existe on active disable,
      // en securite, au cas ou l'objet errors est undefined ou null, ou lui passe un objet vide
      let disable = Object.keys(this.props.errors || {}).length; // si lenght > 0 true sinon false ?
      let error = checkInput(id, values[id], min, max);
      const errors = { ...this.state.errors };
      delete errors[id]; //je supprime la valeur de errors
      if (error) {
        // si error existe
        errors[id] = error; // je l'attribue le lie a sa clef dans l'objet errors
        disable = true; // et j'active disable
      }
      this.setState({ errors }); // j'enregistre errors
      this.setState({ disable }); // j'enregistre disable
    });
  };

  handleChange = e => {
    let files = e.target.files;
    console.log(files);
  };

  submit = e => {
    e.preventDefault();
    this.props.onSubmit();
  };

  render() {
    const form = (
      <Form data-test="form" className="login-form eiga-form" onSubmit={this.submit}>
        <div className="login-form-content">
          {this.state.form.map(input => (
            <MyInput data-test="input"
              key={input.label}
              controlId={input.controlId}
              type={input.type}
              label={input.label}
              value="mehdi123"
              error={this.state.errors[input.controlId]}
              changed={(e) => this.changedHandler(e, input.min, input.max)}
            />
          ))}
            {/* <Avatar /> */}
            <Form.Text className="mt-3">
                {/* Deja membre ?<NavLink className='Link' to="/connexion"> Connectez-vous</NavLink> */}
            </Form.Text>
            <MyButton data-test="button" disable={this.state.disable} />
        </div>
      </Form>
    );

    return (
      <div className="Page">
        <div className="Register">
          <h1 className="login-form-title">Inscription</h1>
          {form}
        </div>
      </div>
    );
  }
}

Register.propTypes= {

};

export default Register;
