import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import * as actions from '../../../../store/actions/index';
import firebase from '../../../../fire';
import './PhotoUser.css';
import { Component } from 'react';

let id = localStorage.getItem('id');
let fileName = 'image';
let newDirectory = id;
let storage = firebase.storage().ref(`images/${newDirectory}/${fileName}`);

class PhotoUser extends Component {
    state = {
        image: null,
        imageTmp: null,
        good: false
    }

    componentDidMount() {
        console.log(fileName)
        if (localStorage.getItem('photo')) {
            this.setState({image : localStorage.getItem('photo')})
            // photoProfil(localStorage.getItem('photo'));
        }
        else if (localStorage.getItem('photoPhone')){
            this.setState({image : localStorage.getItem('photoPhone')})
            // photoProfil(localStorage.getItem('photoPhone'));
        }
        else {
            storage.getDownloadURL()
                .then(function(url) {
                    if (url) {
                        this.setState({image: url});

                        // photoProfil(url);
                        // setImage(url);
                    }
                })
                .catch(err => {
                    // setImage('https://lebackyard.fr/wp-content/plugins/all-in-one-seo-pack/images/default-user-image.png');
                })
        }
    }


    handleChange = (e) => {
        if (e.target.files[0]) {
            if (e.target.files[0] !== this.state.image ) {
                this.setState({imageTmp: e.target.files[0], good: true})
            }            
        }         
    };

    handleUpload = async () => {
        if(this.state.good) {
            if (this.state.image !== undefined) {
                await storage.put(this.state.imageTmp)
                    .then(res => { 
                       // console.log(res)
                    })
                    .catch(err => {
                        // console.log(err)
                    })
            }
            await storage.getDownloadURL()
                .then(function(url) {
                    // photoProfil(url);
                    this.setState({image:url, good: false})            
                })
                .catch(err => {
                    // console.log(err)
                })
        }        
    }
    render () {
        return (
            <div className="BlockContainer">
                <div className="TitleBackground">
                    <h4 className='h4'>Photo de Profil</h4>
                </div>
                <div className="BlockImageProfil">
                    {/* <img src={this.stateimage} className='ImgProfil'alt=''/>
                    { (!photoSocial && !photoPhone) && 
                    <div className="BlockButton">
                        <input style={{color:'black', cursor:'pointer'}} type='file' accept="image/*" onChange={handleChange} />
                        <button className="ButtonUpload" onClick={handleUpload}>Upload</button>
                    </div> }     */}
                </div>
            </div>
        )
    }

}

export default PhotoUser;
