import React, { Component } from "react";
import InfiniteScroll from "react-infinite-scroll-component";
import { connect } from "react-redux";
import "./MoviesList.css";
import MovieItems from "./MoviesItems/MoviesItems";
import * as actions from '../../../store/actions/index';

import axios from 'axios';

class MoviesList extends Component {

  state = {

  }
  componentDidMount () {
    // this.props.popular(this.props.page);
  }

  // componentDidUpdate (prevProps) {
  //   if (prevProps.movies !== this.props.movies) {
  //     console.log('OKKKKKKKKKKKKKKKKKK');
  //     console.log(prevProps.movies);
  //     console.log(this.props.movies);
  //   }
  // };

  newMovie = async () => {
    try {
      if (this.state.nbPage !== 0 && this.state.page > this.state.nbPage) {
        this.setState({ hasMore: false });
        return;
      }
      let url = `${PATH_BASE}${PATH_DISCOVER}${PATH_MOVIE}${API_KEY}${PATH_PAGE}${this.state.page}${PATH_ADULT}${PATH_LANGUE}fr`;
      let response = await axios.get(url);
      this.setState({ nbPage: response.data.total_pages }, () => {
        this.setMovies(response.data.results);
      });
    } catch (error) {
      console.log(error);
    }
  };

  render() {
    let movies = null;
    if (this.props.movies !== null && this.props.movies.length > 0) 
      movies = <MovieItems movies={this.props.movies} clicked={() => this.clicked()}/>
    if (this.props.movies.length === 0)
      movies = <p style={{ color: "white" }}>Il y a 0 resultat.</p>

    return (
      <div className="block">
        <InfiniteScroll
          dataLength={this.props.movies.length}
          // next={this.props.popular(this.props.page)}
          hasMore={this.props.hasMore}
          loader={<h4>Loading...</h4>}
          endMessage={<p style={{ textAlign: "center" }}><b>Yay! Vous avez tout vu</b></p>}>
          {movies}
        </InfiniteScroll>
      </div>
    );
  }
}

const mapStateToProps = state => {
  console.log('moviesList', state)
  return {
    page: state.filtres.page,
    hasMore: state.filtres.hasMore,
    movies: state.filtres.movies,
  };
};

const mapDispatchToProps = dispatch => {
  return {
    popular: (page) => dispatch(actions.popular(page))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(MoviesList);
