import React, { useEffect, useState } from 'react';
import { Carousel } from 'react-bootstrap';
import axios from 'axios';

const MyOrder = () => {
    const [orderUser, setOrderUser] = useState([]);
    useEffect(() => {
        axios.get(`https://movies-27cd5.firebaseio.com/${localStorage.getItem('id')}/Order.json/`)
        .then(res => {
            console.log(res.data)
            if (res.data !== null) 
                setOrderUser(res.data)
        })
        .catch(err => {})   
    }, [])

    let order = orderUser.map(order=> {
        {console.log('orderUser -->', orderUser)}
        return (
            <div key={order.numberOrder}className="BlockContainer" style={{width:'375px'}}>
                <div className="TitleBackground">
                    <h4 className='h4'>Commande {order.numberOrder} du {order.date}</h4>
                </div>
                <Carousel className="Slider" style={{height:'400px', backgroundColor:'grey'}}indicators={order.films.length > 1 ? true : false} controls={order.films.length > 1 ? true : false} interval={5000}>
                    {order.films.map(movie => (  
                        <Carousel.Item  key={movie.id}>
                            <img style={{width:'275px', height:'400px', margin:'auto'}} src={`https://image.tmdb.org/t/p/w500${movie.img}`} alt={movie.id} />
                        </Carousel.Item>
                    ))}
                 </Carousel>
            </div>
            )
        }
    )

    return (<>{order}</>)
}

export default MyOrder;
