import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import './Profil.css'
import { BiIdCard } from 'react-icons/bi'
import { useLocation } from 'react-router-dom';


const MyProfil = ({child}) =>  {
    const [color1, setColor1] = useState('LiProfil');
    const [color2, setColor2] = useState('LiProfil');
    const [color3, setColor3] = useState('LiProfil');

    function Params () {
        let params = useLocation();
        useEffect (() => {

            console.log(params)
            // if (child.type.name='MyInfo')
            //     setColor1('LiClick')
            // else if (child.type.name='MyOrder')
            //     setColor2('LiClick')
            // else if (child.type.name='MyList')
            //     setColor3('LiClick')
        }, [params])
    }



    const clicked = (e) => {
        setColor1('LiProfil');
        setColor2('LiProfil');
        setColor3('LiProfil');
        if (e.target.id === '1') 
            setColor1('LiClick');
        if (e.target.id === '2')
            setColor2('LiClick');
        if (e.target.id === '3')
            setColor3('LiClick');
    };
    Params ();
    return (
        <div className="PageProfil">
            <div className="NavProfil">
                <div className="MyAccount">
                    Mon Compte
                </div>
                <ul className="UlProfil" >
                    <NavLink to="/profil/" className='NavLinkProfil'>
                        <BiIdCard style={{color:'black'}}/>
                        <li className={color1} id='1'>Mes informations</li>
                    </NavLink>
                    <NavLink to="/profil/MyOrder" style={{textDecoration:'none'}}>
                        <li className={color2} id='2'>Mes commandes</li>
                    </NavLink>
                    <NavLink to="/profil/MyList" style={{textDecoration:'none'}}>
                        <li className={color3} id='3' >Ma Liste</li>
                    </NavLink>
                </ul>
            </div>

            <div className="Children">
                {child}
            </div>
        </div>
    );
};

export default MyProfil;
