import React, { Component } from "react";
import InfiniteScroll from "react-infinite-scroll-component";
import { connect } from "react-redux";
import "./MoviesList.css";
import { withRouter } from 'react-router-dom';
import MovieItems from "./MoviesItems/MoviesItems";
import * as actions from '../../../store/actions/index';

class MoviesList extends Component {
  componentDidMount () {
    this.props.popular();
  }

  render() {
    let movies = null;
    if (this.props.movies !== null && this.props.movies.length > 0) 
      movies = <MovieItems movies={this.props.movies} clicked={() => this.clicked()}/>
    if (this.props.movies.length === 0)
      movies = <p style={{ color: "white" }}>Il y a 0 resultat.</p>

    let asyncPagination = null;

    return (
      <div className="block">
        <InfiniteScroll
          dataLength={this.props.movies.length}
          next={asyncPagination}
          // hasMore={this.state.hasMore}
          loader={<h4>Loading...</h4>}
          endMessage={<p style={{ textAlign: "center" }}><b>Yay! You have seen it all</b></p>}>
          {movies}
        </InfiniteScroll>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    movies: state.filtres.movies,
  };
};

const mapDispatchToProps = dispatch => {
  return {
    popular: () => dispatch(actions.popular())
  };
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(MoviesList));
