import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../utility';

const initialState = {
    cart: [
        {
            title: 'FILM 1',
            price: 1000,
            id: 1,
            qte : 1
        },
        {
            title: 'FILM 2',
            price: 2000,
            id: 2,
            qte: 1
        },
        {
            title: 'FILM 3',
            price: 3000,
            id: 3,
            qte: 1
        },
        {
            title: 'FILM 4',
            price: 4000,
            id: 4,
            qte: 1
        },
    ],
    total: 0,
    amount: 0
};

const addToCart = (state, action) => {
  return updateObject( state, { 
    count: state.count + 1
  })
}

const removeToCart = (state, action) => {
    console.log(action.id)
    return updateObject( state, { 
        cart: state.cart.filter((cartItem) => cartItem.id !== action.id)
    })
}

const resetCart = (state, action) => {
    // <=> return{ ...state, count: 0 }.
    return updateObject( state, { 
      cart: []
    })
}

const reducer = ( state = initialState, action ) => {
  switch ( action.type ) {
    case actionTypes.ADD_TO_CART: return addToCart(state, action);
    case actionTypes.REMOVE_TO_CART: return removeToCart(state, action);
    case actionTypes.RESET_CART: return resetCart(state, action);
    default: return state;
  }
}; 

export default reducer;
