import React, {Component} from 'react'
import {Button, Form} from 'react-bootstrap'
import firebase from '../../fire';
import * as regex from './Regex'
import { withRouter } from 'react-router-dom';
import * as actions from '../../store/actions/index';
import { connect } from 'react-redux';


class Phone extends Component {
    state = {
        phone:null,
        codeVerification:null,
        error:{},
        error1:{},
        errorCatch:'',
        disable:true,
        disable1:true,
        confirmResult: null,
        show: false,
        codeError:null
    }

    handleFormValidcode = () => { 
        let store = ''; 
        const error = {...this.state.error1};
        const values = Object.values(error)
        for (const key of values)
            store += key;
        Object.keys(error).length===1 && store.length===0? this.setState({disable1:false}):this.setState({disable1:true},()=>{console.log(this.state.disable1)});
}

    handleFormValidphone = () => { 
            let store = ''; 
            const error = {...this.state.error};
            const values = Object.values(error)
            for (const key of values)
                store += key;
            Object.keys(error).length===1 && store.length===0? this.setState({disable:false}):this.setState({disable:true},()=>{console.log(this.state.disable)});
    }

    handleInputValid = (name_input,value_input) => {
        let error = {...this.state.error};
        let error1 ={...this.state.error1}
        switch(name_input){
            case 'phone': value_input.match(regex.phoneNumber)  ? error[name_input]='' : error[name_input] = "*votre numero nest pas valid";
                break;
            case 'codeVerification':value_input.match(regex.codeVerification) && value_input.length>5 ? error1[name_input]='' : error1[name_input] = "*mauvais format 5chiffre sont attendues";
                 break;
            default:
                console.log("NUMBER NOT FOUND");
        }
        this.setState({error:error}, () => {this.handleFormValidphone()});
        this.setState({error1:error1}, () => {this.handleFormValidcode()});
    }

    handleInput = (event) => {
        const nameInput = event.target.id;
        const valueInput = event.target.value;
        console.log(nameInput,valueInput)

        this.setState({[event.target.id]:event.target.value},
            () => {this.handleInputValid(nameInput,valueInput)    
        });
    }

    sendCode = (e) => {  
        e.preventDefault();
        firebase.auth().languageCode = 'fr';
        window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier("recaptcha-container",
        {
           size:"invisible"
        });
        let phoneNumber = this.state.phone;
        let appVerifier = window.recaptchaVerifier;
        firebase.auth().signInWithPhoneNumber(phoneNumber, appVerifier)
        .then(confirmationResult => { 
            this.setState({show:true})
            window.confirmationResult = confirmationResult;
        })
        .catch(error => {
            this.setState({errorCatch:error.message})
        });
    }

    receiveCode= (e) => {
        e.preventDefault();
        const verificationId = this.state.codeVerification;
        window.confirmationResult
        .confirm(verificationId)
        .then(result => {
            localStorage.setItem('id',result.user.uid)
            localStorage.setItem('token', result.user.ma)
            this.props.onPhone(result.user.ma,result.user.uid);
            this.props.history.push('/home')
        })
        .catch(error => {
            this.setState({codeError:'Error lors de la verification du code: code invalide'})
        });
    }

    render() {
        let msg;
        let form;
        let msgCatch;


        if (this.state.errorCatch && this.state.codeError === null)
            msgCatch = <p>Trop d'essai votre compte a été suspendu temporairement.</p>

        if (this.state.codeError)
            msgCatch = <p>le code ne coresspond pas a celui qui vous a été envoyer veuillez ressayer</p>

        if (this.state.show && this.state.codeError === null)
            msgCatch = <p>Un code vient de vous etre envoyer par SMS.</p>

        if (this.state.show === false){
            form = (
                <Form.Group>
                    <Form.Label>Connexion via Smarthpone</Form.Label> 
                    <Form.Control id="phone" type="tel" placeholder="Enter Phone-number ex :+33625145895"   onChange={(e)=>this.handleInput(e)}/>
                    <Form.Text className="text-muted">         
                        {this.state.error.phone}     
                    </Form.Text>
                    <Button id="recaptcha-container" disabled={this.state.disable} onClick={(e)=>this.sendCode(e)}>
                        Envoyer code
                    </Button>
                </Form.Group>
            )
        }

        if (this.state.show){
            form = (
                <Form.Group>
                    <Form.Label>Connexion via Smarthpone</Form.Label>
                    <Form.Control id="codeVerification" type="tel" placeholder="Entrer le code de verification" onChange={(e)=>this.handleInput(e)} />
                    <Form.Text className="text-muted">         
                        {this.state.error1.codeVerification}   
                    </Form.Text>
                    <Button id="code" disabled={this.state.disable1} onClick={(e)=>this.receiveCode(e)}>
                        S'inscrire
                    </Button> 
                </Form.Group>
            )
        }

        return (
            <Form onSubmit={this.onSubmit}>

                {form}
                {form}

                {msgCatch}
            </Form>   
        )
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onPhone: (token, userId) => dispatch(actions.authSuccess(token, userId))
    };
};

export default withRouter(connect(null, mapDispatchToProps) (Phone));
