import React, { useEffect, useState } from 'react';
import Slider from '../../Movie/Slider/Slider';
import axios from 'axios';

const MyOrder = () => {
  const [orderUser, setOrderUser] = useState([]);
  useEffect(() => {
    axios.get(`https://movies-27cd5.firebaseio.com/${localStorage.getItem('id')}/Order.json/`)
      .then(res => {
        if (res.data !== null) 
          setOrderUser(res.data)
      })
      .catch(err => {})   
  }, [])

  let order = orderUser.map(order => (
    <>
      <h1 className='Title'>Mes Commandes</h1>
      <Slider similar={order.films} title={`Commande ${order.numberOrder} du ${order.date}`}/>
    </> 
    )
  )
  return (<>{order}</>)
}

export default MyOrder;
