import React, { useEffect, useState } from 'react';
import Spinner from '../../components/Spinner/Spinner';
import DetailsCommand from './DetailsOrder/DetailsOrder';

const ConfirmOrder = () => {
    const [load,setLoad] = useState(true);

    useEffect(() => {
        let timer;
        if (localStorage.getItem('spinner') === null){
            timer = setTimeout(() => {
                setLoad(false)
                localStorage.setItem('spinner',true)
            }, 300000);
        }
        return (() => {
            clearTimeout(timer);
            localStorage.removeItem('spinner');
            localStorage.removeItem('commandeSuccess');
        }) 
    }, [])

    return (<>
            { load && localStorage.getItem('spinner') === null ? <div style={{minHeight:'100vh', display:'flex', alignItems:'center', justifyContent:'center'}}><Spinner css="SpinnerOrder" text="Veuillez patienter , paiement en cours"/></div> : <DetailsCommand />}</>
    )}
export default ConfirmOrder;
