import * as actionTypes from './actionTypes';
import axios from 'axios';
import { API_KEY, PATH_BASE, PATH_DISCOVER, PATH_MOVIE, PATH_SEARCH, PATH_PAGE, PATH_ADULT, PATH_LANGUE } from  '../../containers/Home/MoviesList/api';

export const movies = (movies, nbPage) => {
    return {
        type: actionTypes.MOVIES,
        newMovies: movies,
        nbPage: nbPage,
    }
};

export const popular = (page) => {
    return dispatch => {
        axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=1e32f5c452c2267d5367589e9864ab1c&language=fr&page=${page}`)
            .then(response => {
                dispatch(movies(response.data.results, response.data.total_pages));
            })
            .catch(err => console.log(err));
    }
};

export const filtres = (filtres) => {
    return dispatch => {
        let url = `${PATH_BASE}${PATH_DISCOVER}${PATH_MOVIE}${API_KEY}${PATH_PAGE}1${PATH_ADULT}${PATH_LANGUE}fr`;
        if (!!filtres.genre.value)
            url += `&with_genres=${filtres.genre.value}`;
        if (!!filtres.years.value)
            url += `&primary_release_year=${filtres.years.value}`;
        if (!!filtres.sortby.value)
            url += `&sort_by=${filtres.sortby.value}`;
        axios.get(url)
            .then(response => {
                dispatch(movies(response.data.results));
            })
            .catch(err => console.log(err));
    }
};

export const textSearch = (textSearch) => {
    return dispatch => {
        if (!!textSearch) {
            axios.get(`${PATH_BASE}${PATH_SEARCH}${PATH_MOVIE}${API_KEY}${PATH_PAGE}1${PATH_LANGUE}fr${PATH_ADULT}"&query=${textSearch}`)
                .then(response => {
                    dispatch(movies(response.data.results));
                })
                .catch(err => console.log(err));
        }
        else 
           dispatch(popular());
    }
};
