import React, { useEffect, useState }  from 'react';
import axios from 'axios';
import '../MyInfo.css';
import './InfoUser.css';


const InfoUser = () => {

    const [id, setId] = useState('');
    const [idToken, setIdToken] = useState('');
    const [name, setName] = useState('');
    const [lastname, setLastname] = useState('');
    const [login, setLogin] = useState('');
    const [address, setAddress] = useState('');
    const [mail, setMail] = useState('');

    useEffect(() => {
        setIdToken(localStorage.getItem('token'))
        setId(localStorage.getItem('id'))
        let idLocal = localStorage.getItem('id')
        const mail = { mail : localStorage.getItem('mail') };
        axios.get(`https://movies-27cd5.firebaseio.com/${idLocal}/user.json/`)
            .then(response => {
                setAddress(response.data.address) 
                setName(response.data.name)   
                setLastname(response.data.lastname)  
                setLogin(response.data.login)  
            })
            .catch(err => { console.log(err)})
        axios.put(`https://movies-27cd5.firebaseio.com/${idLocal}/mail.json/`,mail)
            .then(response => { setMail(response.data.mail); })
            .catch(error => { console.log('MAILL//',error) })
    },[]) 

     const handleSubmitMail = (e) => {
        e.preventDefault(); 
        const authData = {
            idToken:idToken,
            email: mail,
            returnSecureToken: true
        };
        axios.post('https://identitytoolkit.googleapis.com/v1/accounts:update?key=AIzaSyDJQ2C-WHsJXu5xVCG5Z98XQ31gRJrSV_E',authData)
            .then(response => { 
                const mail = { mail : response.data.email };
                axios.put(`https://movies-27cd5.firebaseio.com/${id}/mail.json/`,mail)
                    .then(response =>{ console.log('MailResponse',response) })
                    .catch(error => { console.log('MailResponse',error) })
            })
            .catch(err => {
                console.log('handleSubmitMail error FormUser.jsx',err.response.data.error.message)
            })      
    }

    const handleSubmit =(event) => {
        event.preventDefault(); 
        const data = {
            name:name,
            lastname: lastname,
            login: login,
            address: address,
        };
        axios.put(`https://movies-27cd5.firebaseio.com/${id}/user.json/`,data)
            .then(response => {
                console.log('data',response);  
            })
            .catch(err => {
                console.log('data',err.response)
            })    
    }

    return (
        <>
            <div className="BlockContainer">
                <div className="TitleBackground">
                    <h4 className='h4'>Informations Personelles</h4>
                </div>
                <div className="BlockUserInfo">
                    <div className='Duo'>
                        <input className='Input' type="mail" id="mail" placeholder="Mail" />
                        <div className='Error'></div>
                    </div>
                </div>
            </div>

            <div className="BlockContainer">
                <div className="TitleBackground">
                    <h4 className='h4'>Informations Personelles</h4>
                </div>
                <form onSubmit={handleSubmit} className="BlockUserInfo">
                    <div className='Duo'>
                        <input className='Input' type="text" id="nom" placeholder="Nom" />
                        <div className='Error'></div>
                    </div>
                    <div className='Duo'>
                        <input className='Input' type="text" id="prenom" placeholder="Prenom" />
                        <div className='Error'></div>
                    </div>
                    <div className='Duo'>
                        <input className='Input' type="text" id="adresse" placeholder="Adresse" />
                        <div className='Error'></div>
                    </div>
                    <button>VALIDER</button>
                </form>
            </div>
        </>
    )
}

export default InfoUser;
