import React, { Component } from 'react';

import { checkInput } from "../../../components/utility/utility";

import MyInput from "../../../components/MyInput/MyInput";
import MyButton from '../../../components/MyButton/MyButton';

import { Form } from 'react-bootstrap';
import '../auth.css';

import firebase from '../../../fire';

class FogertPwd extends Component {
    state = {
        errors: {},
        values: {},
        disable : true
    }

    changedHandler = (e, min, max) => {
        const Id = e.target.id;
        const values = {...this.state.values};
        values[Id] = e.target.value;

        this.setState({values : values}, () => {
          let error = null; 
          error = checkInput(Id, values[Id], min, max);
          const errors = {...this.state.errors};
          errors[Id] = error;
          this.setState({errors: errors}, () => {Object.values(this.state.errors).join('').length === 0 && Object.keys(this.state.errors).length === 1 ? this.setState({disable: false}) : this.setState({disable: true});})
        });
    };

    submit = (e) => {
        e.preventDefault();
        console.log('siuuuuuuu',this.state)
        firebase.auth().sendPasswordResetEmail(this.state.values.mail).then(response =>{
            console.log(response);
        })
        .catch(response => {
           console.log(response)
        })
    };

    render () {
        const form = (
            <Form className="login-form eiga-form" onSubmit={this.submit}>
                <div className="login-form-content">
                    <Form.Text>
                        Saisissez l’adresse email de votre compte afin de recevoir des instructions pour réinitialiser votre mot de passe.
                    </Form.Text>
                    <MyInput
                        data-test="Email"
                        key="Email"
                        controlId="mail"
                        type="text"
                        label="Email"
                        // value={this.state.value[input.controlId]}
                        error={this.state.errors["mail"]}
                        changed={(e) => this.changedHandler(e, 4, 40)} />
                    <MyButton data-test="button" disabled={this.state.disabled} />
                </div>
            </Form>
        )
        return (
            <div className="Page">
                <div className="Register">
                    <div className="Login-wrapper">
                        <h1 className="login-form-title">Mot de passe oublié ?</h1>
                        {form}
                    </div>
                </div>  
            </div>
        );
    };
};

export default FogertPwd;
