import React, { Component } from 'react';
import Sidebar from './Sidebar/SideBar';
import MoviesList from './MoviesList/MoviesList';
import './Home.css';

class Home extends Component {
    state = {
        inputValue: '',
        search: null
    };

    handleSearchChange = (e) => {
        this.setState({inputValue: e.target.value});
    };

    render () {
        return (
            <div className="Page">
                <div className="Gauche">
                    <Sidebar changed={this.handleSearchChange} clicked={this.clicked}/>
                </div>
                <div className="Droite">
                    <MoviesList textSearch={this.state.inputValue}/>
                </div>
            </div>
        );
     };
};

const mapStateToProps = state => {
    return {
        errorServor: state.auth.errorServor,
        registerSuccess: state.auth.registerSuccess
    };
};

const mapDispatchToProps = dispatch => {
    return {
        initRegisterSuccess: (value) => dispatch(actions.registerSuccess(value)),
    };
};

export default connect(mapStateToProps, mapDispatchToProps) (Home);
