import * as actionTypes from './actionTypes';
import axios from 'axios';

import { API_KEY, PATH_BASE, PATH_DISCOVER, PATH_MOVIE, PATH_SEARCH, PATH_PAGE, PATH_ADULT, PATH_LANGUE } from  '../../containers/Home/MoviesList/api';

export const movies = (movies) => {
    return {
        type: actionTypes.MOVIES,
        movies: movies
    }
};


export const filtres = () => {
    return dispatch => {
        axios.get('https://api.themoviedb.org/3/discover/movie?api_key=1e32f5c452c2267d5367589e9864ab1c&with_genres=28')
            .then(response => {
                dispatch(movies(response.data.result));
            })
            .catch(err => console.log(err))
    }
}

export const textSearch = (textSearch) => {

    return dispatch => {
        axios.get(`${PATH_BASE}${PATH_SEARCH}${PATH_MOVIE}${API_KEY}${PATH_PAGE}1${PATH_LANGUE}fr${PATH_ADULT}"&query='sonic'`)
        .then(response => {
            console.log('4444444  --->  ', response)
            dispatch(movies(response.data.result));
        })
        .catch(err => console.log(err))
    }
}
