import React from 'react';
import { Row, Col } from 'react-bootstrap';
import { FaUserCircle} from 'react-icons/fa';

const Rubrique = (props) => {
    let data = null;

    if (props.map) {
        console.log(props.data)
        data = (
            <Row className="LuMovie">
                {
                    props.data.map(data => {
                        return (
                            <Col sm={3} key={data.id} style={{display:'flex', flexDirection:'column',}}>
                                { data.profile_path && props.img ? 
                                    <img style={{borderRadius:'50%', margin:'auto'}} src={`https://image.tmdb.org/t/p/w100_and_h100_face/${data.profile_path}`} alt='lol'/> : 
                                    <FaUserCircle style={{borderRadius:'50%', margin:'auto', width:'100px', height:'100px'}}/> }
                                <p className={props.css} style={{textAlign:'center'}}>{data.name}</p>
                            </Col>
                        )
                    })
                }
            </Row>
        );
    }
    else
        data = props.data;

    return (
        <Row>
            <Col>
                {props.data.length !== 0 && <p className='TitreMovie'>{props.title}</p>}
                {data} 
            </Col>
        </Row>
    )
}

export default Rubrique;
