import React from 'react';
import { useSelector } from 'react-redux';
import { AiOutlineCloseCircle } from 'react-icons/ai';
import './ShoppingCart.css';

const ShoppingCart = () => {

    const movies = useSelector(state => state.movies.movies.splice(0, 5));
    const cart = (
        <>
        <ul style={{padding:'0'}}>
            {movies.map(movie => (
                <li className="BlockFilm" key={movie.id}>
                    <img style={{width:'150px', height:'200px'}} src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`} alt={movie.id}/>
                    <div className="InfoFilm">
                        <h4 style={{color:'grey'}}>{movie.title}</h4>
                        <p style={{color:'black', fontWeight:'bold'}}>10$</p>
                    </div>
                    <div>
                        <AiOutlineCloseCircle className="DeleteProduct"/>
                    </div>
                </li>))}

        </ul>
            <div className="Checkout">
            <p>Total: 456$</p>
            <button>Valider</button>
        </div>
        </>
    )

    return (
        <div className="PageCart">
            <div className="BlockCart">
                <h1 className='Title'>Mon Panier <span style={{fontStyle:'italic'}}>({movies.length} articles)</span></h1>
                {cart}

            </div>
        </div>
    )
}

export default ShoppingCart;
